 enum Either {
     One(X),
     Two(X),
+    Three { a: X },
 }

 struct X(Y);

 struct Y;

+struct Z(*const i32);
+unsafe impl Send for Z {}
+
+enum Meow {
+    A { a: Z },
+    B(Z),
+}
+
 fn consume_fnmut(_: impl FnMut()) {}

